mapper.update:
update contest
title = #{title}, description = #{description}, start_time = #{startTime}, end_time = #{endTime}, state = #{state}, visibility = #{visibility}, password_hash = #{passwordHash}, updated_at = #{updatedAt},
where contest_id = #{contestId}

mapper.select:
SELECT c.contest_id, c.title, c.description, c.start_time, c.end_time, c.visibility, CASE WHEN c.end_time < #{now} THEN 'ENDED' WHEN c.start_time > #{now} THEN 'SCHEDULED' ELSE 'ONGOING' END AS status, COUNT(cp.user_id) AS participant_count FROM contest c LEFT JOIN contest_participant cp ON c.contest_id = cp.contest_id
AND c.title LIKE CONCAT('%', #{query.keyword}, '%')
AND c.visibility = #{query.visibility}
AND c.state = #{query.state}
AND c.start_time <= #{now} AND c.end_time >= #{now}
AND c.start_time > #{now}
AND c.end_time < #{now}
GROUP BY c.contest_id ORDER BY c.${sortColumn} ${sortDirection}
