mapper.update:
update training_plan
title = #{title}, description = #{description}, start_time = #{startTime}, end_time = #{endTime}, updated_at = #{updatedAt}
where plan_id = #{planId}

mapper.select:
SELECT tp.plan_id, tp.title, tp.start_time, tp.end_time, tp.creator_id, -- 优化1: 直接在SQL中计算动态状态，简化Java代码 CASE WHEN tp.end_time < #{now} THEN 'ENDED' WHEN tp.start_time > #{now} THEN 'SCHEDULED' ELSE 'ONGOING' END AS status, -- 优化2: 使用 LEFT JOIN 和 COUNT(DISTINCT) 解决 N+1 性能问题 COUNT(DISTINCT tps.user_id) AS student_count, COUNT(DISTINCT tpc.contest_id) AS contest_count FROM training_plan tp LEFT JOIN training_plan_student tps ON tp.plan_id = tps.plan_id LEFT JOIN training_plan_contest tpc ON tp.plan_id = tpc.plan_id
AND tp.title LIKE CONCAT('%', #{query.keyword}, '%')
AND tp.creator_id = #{query.creatorId}
AND tp.start_time > #{now}
AND tp.start_time <= #{now} AND tp.end_time >= #{now}
AND tp.end_time < #{now}
AND tps.user_id = #{userId}
AND tp.creator_id = #{userId}
AND 1 = 0
-- 必须 GROUP BY, 因为使用了聚合函数 COUNT() GROUP BY tp.plan_id -- 优化4: 实现安全的动态排序 ORDER BY ${sortColumn} ${sortDirection}
